import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({ username: '', password: '', userType: '' });
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch users from the server
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/users');
                setUsers(response.data);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    const handleAddUser = async () => {
        try {
            await axios.post('http://localhost:5000/api/users', newUser);
            setNewUser({ username: '', password: '', userType: '' }); // Reset form
            // Refresh the user list
            const response = await axios.get('http://localhost:5000/api/users');
            setUsers(response.data);
        } catch (error) {
            console.error('Error adding user:', error);
        }
    };

    return (
        <div className="p-8">
            <h2 className="text-2xl font-bold mb-4">Manage Users</h2>
            <div className="mb-8">
                <h3 className="text-xl font-semibold mb-2">Add New User</h3>
                <div className="space-y-4">
                    <input
                        type="text"
                        placeholder="Username"
                        value={newUser.username}
                        onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                        className="block w-full border border-gray-300 rounded-md p-2"
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={newUser.password}
                        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                        className="block w-full border border-gray-300 rounded-md p-2"
                    />
                    <select
                        value={newUser.userType}
                        onChange={(e) => setNewUser({ ...newUser, user_type: e.target.value })}
                        className="block w-full border border-gray-300 rounded-md p-2"
                    >
                        <option value="">Select User Type</option>
                        <option value="admin">Admin</option>
                        <option value="user">User</option>
                    </select>
                    <button 
                        onClick={handleAddUser}
                        className="bg-purple-900 text-white py-2 px-4 rounded"
                    >
                        Add User
                    </button>
                </div>
            </div>
            <div>
                <h3 className="text-xl font-semibold mb-2">Users List</h3>
                <table className="min-w-full bg-white border border-gray-300 rounded-lg shadow-md">
                    <thead>
                        <tr>
                            <th className="px-4 py-2 border-b">Username</th>
                            <th className="px-4 py-2 border-b">User Type</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user._id}>
                                <td className="px-4 py-2 border-b">{user.username}</td>
                                <td className="px-4 py-2 border-b">{user.user_type}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ManageUsers;
