const pageVariants = {
    initial: {
        opacity: 0,
        scale: 0.95,
        x: -100,
    },
    in: {
        opacity: 1,
        scale: 1,
        x: 0,
    },
    out: {
        opacity: 0,
        scale: 1.05,
        x: 100,
    },
};

const PageTransition = ({ children }) => {
    return (
        <motion.div
            initial="initial"
            animate="in"
            exit="out"
            variants={pageVariants}
            transition={{ duration: 0.6, type: 'spring', stiffness: 300 }}
        >
            {children}
        </motion.div>
    );
};
