import React from 'react';
import { motion } from 'framer-motion';

const pageVariants = {
    initial: {
        opacity: 0,
        x: "100vw",
        y: "100vh",
    },
    enter: {
        opacity: 1,
        x: 0,
        y: 0,
    },
    exit: {
        opacity: 0,
        x: "-100vw",
        y: "-100vh",
    },
};

const PageTransition = ({ children }) => {
    return (
        <motion.div
            initial="initial"
            animate="enter"
            exit="exit"
            variants={pageVariants}
            transition={{ duration: 0.6, ease: 'easeInOut' }}
        >
            {children}
        </motion.div>
    );
};

export default PageTransition;
