// src/components/Header.js
import React, { useState } from 'react';
import logo from '../images/logo.png'; // Import the logo

const Header = () => {
    const [dropdownVisible, setDropdownVisible] = useState(false);
    const username = localStorage.getItem('username');

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        localStorage.removeItem('username');
        window.location.href = '/';
    };

    const toggleDropdown = () => {
        setDropdownVisible(!dropdownVisible);
    };

    return (
        <header className="flex justify-between items-center p-4 bg-gray-100 shadow-md">
            <div className="flex items-center">
                <img src={logo} alt="INSTAHR Logo" className="h-10 w-10 mr-2" />
                <h1 className="text-xl font-bold text-gray-800">INSTAHR</h1>
            </div>
            {username && (
                <div className="relative">
                    <button 
                        onClick={toggleDropdown} 
                        className="text-lg font-medium text-gray-800 focus:outline-none"
                    >
                        {username}
                    </button>
                    {dropdownVisible && (
                        <div className="absolute right-0 mt-2 w-48 hover:bg-red-600 hover:text-white rounded-lg shadow-lg z-10">
                            <ul className="py-1">
                                <li>
                                    <button 
                                        onClick={handleLogout} 
                                        className="block w-full text-left px-4 py-2 text-sm text-red-600"
                                    >
                                        Logout
                                    </button>
                                </li>
                            </ul>
                        </div>
                    )}
                </div>
            )}
        </header>
    );
};

export default Header;
